import React, { Component } from 'react';
import './css/Projects.css'
import { Link } from 'react-router-dom';
import Header from './Header';
import About from './About';
import Footer from './Footer';
import { projectsList } from '../db';

class Projects extends Component {
  render() {
    const { project } = this.props;
    const render = projectsList.find((pro) => pro.name === project)
    return (
      <>
      <Header />
      <About />
        <section className="boxSkills">
              <h4 className="tamanhoc"><strong>{ render.name }</strong></h4>        
            <div className="boxTech" >
              <div className="haha2">
                <iframe src={ render.demo.img } title={ render.name } className="imgProject" allowfullscreen/>
              </div>
              <div className="haha3">
              <span className="card-describe describe-project">{ render.describe }</span>
                <div className="box-btn">
                  <Link to="/">
                    <span className="btn btnProject">Voltar ao inicio</span>
                  </Link>
                  <a href={ render.href }><span className="btn btnProject">Repositório</span></a>
                  <br />
                  { render.demo.video ? <a href={ render.web }><span className="btn btnProject">Ver vídeo</span></a> : null }
                  { render.web ? <a href={ render.web }><span className="btn btnProject">Ver online</span></a> : null }
                </div>
              </div>
              <p className="text-project">
              { render.text }
              </p>
            </div>
        </section>
      <Footer />
    </>
  )
  }
}

export default Projects;
