import React, { Component } from 'react';
import './css/Projects.css'
import { Link } from 'react-router-dom';

class CardsProject extends Component {
  render() {
    const { data } = this.props;
    return (
        <div className="boxTech">
          {
            data.map((project, index) =>
              data.link ? <Link className="tamanhoc2" to={ project.link } key={ index }>
                <span className="card-name">{ project.name }</span>
                <br />
                <span className="card-describe">{ project.describe }</span>
              </Link>
              : <>
                <a href={ data.href } className="card-name">{ project.name }</a>
                <br />
                <a href={ data.href } className="card-describe">{ project.describe }</a>
              </>


            )
          }
        </div>
  )
  }
}

export default CardsProject;
