import React, { Component } from 'react';
import './css/Projects.css'
import { Link } from 'react-router-dom';

class CardsProject extends Component {
  render() {
    const { data } = this.props;
    return (
        <div className="boxTech">
          {
            data.map((project, index) => {
                console.log(project.href)
              if (data.link) {
                return <Link className="tamanhoc2" to={ project.link } key={ index }>
                    <span className="card-name">{ project.name }</span>
                    <br />
                    <span className="card-describe">{ project.describe }</span>
                  </Link>
              }
              return <a className="tamanhoc2" key={ index } href={ data.href }>
                  <p href={ data.href } className="card-name">{ project.name }</p>
                  <br />
                  <p href={ data.href } className="card-describe">{ project.describe }</p>
                </a>
              }
            )
          }
        </div>
  )
  }
}

export default CardsProject;
