import React, { Component } from 'react';
import './css/Projects.css'
import { Link } from 'react-router-dom';
import Header from './Header';
import About from './About';
import Footer from './Footer';

class Projects extends Component {
  render() {
    const { projectsList, project } = this.props;
    const render = projectsList.find((pro) => pro.name === project)
    return (
      <>
      <Header />
      <About />
        <section className="boxSkills">
            <div className="boxTech" key={ render.name }>
              <h4 className="tamanhoc"><strong>{ render.name }</strong></h4>        
              <br />
              <iframe src={ render.demo.img } title={ render.name } className="imgProject" />
              <span className="card-describe">{ render.describe }</span>
              <br />
              <Link to="/">
                <span>Voltar ao inicio</span>
              </Link>
              <br />
              <a href={ render.href }>Reposit√≥rio</a>
            </div>
        </section>
      <Footer />
    </>
  )
  }
}

export default Projects;
