import React, { Component } from 'react';
import './css/Projects.css'
import { Link } from 'react-router-dom';
import Header from './Header';
import About from './About';
import Footer from './Footer';

class Projects extends Component {
  render() {
    const { projectsList, project } = this.props;
    return (
      <>
      <Header />
      <About />
        <section className="boxSkills">
          { projectsList.map((pro, index) => pro.name === project ? 
            <div className="boxTech" key={ pro.name }>
              <h4 className="tamanhoc"><strong>{ pro.name }</strong></h4>        
              <br />
              <span className="card-describe">{ pro.describe }</span>
              <img src={ pro.demo.img } alt={ pro.name } className="icon" />
              <br />
              <Link to="/">
                <span>Voltar ao inicio</span>
              </Link>
              <br />
              <a href={ pro.href }>Repositório</a>
            </div>
          : console.log('não deu')
        )}</section>
      <Footer />
    </>
  )
  }
}

export default Projects;
