import React, { Component } from 'react';
import './css/Projects.css'
import { Link } from 'react-router-dom';

class CardsProject extends Component {
  render() {
    const { data } = this.props;
    return (
        <div className="boxTech">
          {
            data.map((project, index) =>
              project.link ? <Link className="tags-default" to={ project.link } key={ index }>
                  <span className="card-name">{ project.name }</span>
                  <br />
                  <span className="card-describe">{ project.describe }</span>
                </Link>
              : <a className="tags-default" key={ index } href={ project.href }>
                  <span href={ project.href } className="card-name">{ project.name }</span>
                  <br />
                  <span href={ project.href } className="card-describe">{ project.describe }</span>
                </a>
            )
          }
        </div>
  )
  }
}

export default CardsProject;
