import React, { Component } from 'react';
import './css/Projects.css'

class Projects extends Component {
  render() {
    const projects = [{ name: 'MongoDB', describe: 'Querys para buscar dados em um banco de dados NoSQL - MongoDb' }];
    return (
      <section className="boxSkills">
        <div className="boxTech">
          <h4 className="tamanhoc" id="conhecimento"><strong>Conhe√ßa meus projetos:</strong></h4>        
          {
            projects.map((project) =>
              <div className="tamanhoc2">
                <span>{ project.name }</span>
                <br />
                <span>{ project.describe }</span>
              </div>
            )
          }
        </div>
      </section>
  )
  }
}

export default Projects;
