import React, { Component } from 'react';
import './css/Skills.css'

class Skills extends Component {
  render() {
    const techs = ['JavaScript', 'TypeScript', 'React', 'Node.Js', 'MySQL', 'Jest', 'RTL', 'Redux', 'API Context', 'HTML5', 'CSS', 'GIT', 'Metodologias Ágeis', 'Moodle'];
    return (
      <section className="boxSkills">
          <h4 className="tamanhoc" id="conhecimento"><em>Tenho conhecimento em:</em></h4>
        <div className="boxTech">
          {
            techs.map((tech) => 
              <span className="tamanhoc2">{ tech }</span>
            )
          }
        </div>
        <div className="boxTech">
          <h4 id="estudo"><strong>Atualmente estudo na @betrybe os conteúdos de:</strong></h4>
          <ul>
            <li>TDD</li>
              <li className="tamanhoc2">ORM</li>
              <li className="tamanhoc2">JWT</li>
              <li className="tamanhoc2">MongoDB</li>
              <li className="tamanhoc2">Python</li>
          </ul>
        </div>
      </section>
  )
  }
}

export default Skills;
