import React, { Component } from 'react';
import './css/Projects.css'
import { Link } from 'react-router-dom';
import Header from './Header';
import About from './About';
import Footer from './Footer';
import { projectsList } from '../db';

class Projects extends Component {
  render() {
    const { project } = this.props;
    const render = projectsList.find((pro) => pro.name === project)
    return (
      <>
      <Header />
      <About />
        <section className="boxSkills bounce">
          <div className="box-title">
            <h4 className="title-gradient-black title-project pt"><strong>{ render.name }</strong></h4>        
            <span className="card-describe describe-project">{ render.describe }</span>
          </div>
          <div className="boxTech" >
            <div className="box-from-left">
              <iframe src={ render.demo.img } title={ render.name } className="img-project" allowfullscreen/>
            </div>
            <div className="box-end-right">
              <p className="text-project">{ render.text }</p>
              <div className="box-btn">
                <a href={ render.href }><span className="btn btn-project">Repositório</span></a>
                { render.demo.video ? <a href={ render.web }><span className="btn btn-project">Ver vídeo</span></a> : null }
                { render.web ? <a href={ render.web }><span className="btn btn-project">Ver online</span></a> : null }
                <Link to="/">
                  <span className="btn btn-project back">Voltar</span>
                </Link>
              </div>
            </div>
          </div>
        </section>
      <Footer />
    </>
  )
  }
}

export default Projects;
