import { techs, skills } from '../db';
import '../components/css/Skills.css'

class AboutPage extends Component {
  render() {
    return (
    <>
      <Header />
      <About />
      <section className="box-father">
        <div className="box-child">
          <h4 className="title-gradient-black" id="conhecimento"><strong>Tenho conhecimento em:</strong></h4>      
            <CardsSkills data={ techs } />
        </div>
        <div className="box-child">
          <h4 className="title-gradient-black" id="estudo"><strong>Atualmente estudo na @betrybe os conte√∫dos de:</strong></h4>
          <div className="box-child">
            <CardsSkills data={ skills } />
          </div>
        </div>
      </section>
      <Footer />
    </>
  )
  }
}

export default AboutPage;
